import React, { useState } from 'react';
import '../App.css';

const Payment = () => {
  const [showQRCode, setShowQRCode] = useState(false);

  const toggleQR = () => {
    setShowQRCode(!showQRCode);
  };

  return (
    <div className="payment-main-container">
      <div className="payment-content">
        <h1>Payment Options</h1>
        <div className="payment-upi-section">
          <label>Pay through UPI ID</label>
          <input name="UPI" type="text" className="payment-upi-input" />
          <button type="button" className="payment-action-button">Pay</button>
        </div>
        <div className="payment-bank-section">
          <label>Pay through Bank Transfer</label>
          <input name="Bank" type="text" className="payment-bank-input" />
          <button type="button" className="payment-action-button">Pay</button>
        </div>
        <div className="payment-qr-section">
          <label>Pay through QR Code</label>
          <button
            type="button"
            className="payment-qr-toggle"
            onClick={toggleQR}
          >
            {showQRCode ? "Hide QR code" : "Show QR code"}
          </button>
          {showQRCode && (
            <div className="payment-qr-image">
              <img src={`${process.env.PUBLIC_URL}/Images/QRcode.png`} alt="QR Code" />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Payment;
