import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import '../App.css';

const Homepage = () => {
  const navigate = useNavigate();
  const { authData } = useContext(AuthContext);

  console.log('authData:', authData);

  // Extract user role (assuming role is stored as `authData.user.role`)
  const userRole = authData?.user?.role || ''; 

  const navigateTo = (path) => {
    navigate(path);
  };

  return (
    <div className="homepage">
      <div className="buttonContainer">
        <h1>Home</h1>
        <h2>Welcome {authData?.user?.username}</h2>

        {/* Show the Complain button only if the user is NOT an admin */}
        {userRole === 'user' && (
          <button className="btnGreen" onClick={() => navigateTo('/ComplainSubmit')}>Complain</button>
        )}

        <button className="btnGreen" onClick={() => navigateTo('/ComplainDetails')}>Complain Status</button>
        <button className="btnGreen logout" onClick={() => navigateTo('/')}>Logout</button>
      </div>
    </div>
  );
};

export default Homepage;
