import React from 'react'


const Payment = () => {


  const showQR = () => {
    if (document.getElementById('QR-code').
    style.display === 'none') {
        document.getElementById('QR-code').
        style.display = 'block';
    }
     
  };


    

  return (
    <div>
        <div className="Payment">
            <h1>Payment Options</h1>
            <div className="upi">
                <label>Pay through UPI ID</label>
                <input name="UPI" type="text" id='Upi-box' />
                <button type="button" className="btnPrimary">Pay</button>
                
            </div>
            <div className="Bank Transfer">
                <label>Pay through Bank Transfer</label>
                <input name="Bank" type="text" id='Bank-box' />
                <button type="button" className="btnPrimary">Pay</button>
                
            </div>
            <div className="QR Code">
                <label>Pay through QR Code</label>
                <button type="button" className="btnPrimary" id='QR' onClick={showQR()}>Show QR code</button>
                
            </div>
        </div>
    </div>
  )
}

export default Payment;
