import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from '../contexts/AuthContext';
import '../App.css';

const ComplainDetails = () => {
  const { authData } = useContext(AuthContext);
  const [complaints, setComplaints] = useState([]);
  const [startDate, setStartDate] = useState("2025-02-19");
  const [endDate, setEndDate] = useState("2025-02-26");
  const [selectedType, setSelectedType] = useState("");
  const [selectedStatus, setSelectedStatus] = useState("");
  const [selectedZone, setSelectedZone] = useState(authData?.user?.zone || "");
  const [selectedLocality, setSelectedLocality] = useState("");
  const [mobileNumber, setMobileNumber] = useState(authData?.user?.mobileNumber || "");

  useEffect(() => {
    if (!authData?.user?.mobileNumber || !authData?.user?.username) {
      console.error("User data missing:", authData);
      return;
    }

    console.log("authData:", authData);
    console.log("authData.user.mobileNumber:", authData.user.mobileNumber);
    console.log("authData.user.username:", authData.user.username);

    const fetchComplaints = async () => {
      try {
        const requestBody = {
          startDate,
          endDate,
          mobileNumber: authData.user.mobileNumber,
          createdBy: authData.user.username,
          isAdmin: authData.user.isAdmin,
          complaintType: selectedType,
          complaintStatus: selectedStatus,
          zone: selectedZone,
          locality: selectedLocality,
        };

        console.log("Sending request with:", requestBody);

        const response = await fetch("https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/auth/complain", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(requestBody),
        });

        const data = await response.json();
        console.log("API Response:", data);

        if (Array.isArray(data)) {
          setComplaints(data);
          console.log("Complaints set:", data);
        } else {
          console.error("Unexpected API response format:", data);
          setComplaints([]);
        }
      } catch (error) {
        console.error("Error fetching complaints:", error);
        setComplaints([]);
      }
    };

    fetchComplaints();
  }, [startDate, endDate, selectedType, selectedStatus, selectedZone, selectedLocality, authData]);

  // Function to filter complaints based on selected filters
  const filteredComplaints = complaints.filter((complaint) => {
    const complaintDate = new Date(complaint.createdDate);
    const start = new Date(startDate);
    const end = new Date(endDate);
    return (
      complaintDate >= start &&
      complaintDate <= end &&
      (selectedType ? complaint.type === selectedType : true) &&
      (selectedStatus ? complaint.status === selectedStatus : true) &&
      (selectedZone ? complaint.zone === selectedZone : true) &&
      (selectedLocality ? complaint.locality === selectedLocality : true) &&
      (mobileNumber ? complaint.mobile.includes(mobileNumber) : true)
    );
  });

  console.log("Filtered Complaints:", filteredComplaints);

  return (
    <div className="complaint-details-container">
      <h2>Complaint Details</h2>

      {/* Date Range Section */}
      <div className="flex-container">
        <div>
          <label className="label">Start Date:</label>
          <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="input-box" />
        </div>
        <div>
          <label className="label">End Date:</label>
          <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="input-box" />
        </div>
      </div>

      {/* Filters Section */}
      <div className="flex-container">
        <div>
          <label className="label">Complaint Type:</label>
          <select value={selectedType} onChange={(e) => setSelectedType(e.target.value)} className="dropdown">
            <option value="">Complaint Type</option>
            <option value="electricity">All</option>
            <option value="electricity">Electricity</option>
            <option value="water">Water</option>
            <option value="road">Road</option>
            <option value="waste">Waste</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div>
          <label className="label">Complaint Status:</label>
          <select value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)} className="dropdown">
            <option value="">Complaint Status</option>
            <option value="All">All</option>
            <option value="Open">Open</option>
            <option value="Closed">Closed</option>
          </select>
        </div>
      </div>

      {/* Zone and Locality Filters */}
      <div className="flex-container">
        <div>
          <label className="label">Filter by Zone:</label>
          <select value={selectedZone} onChange={(e) => setSelectedZone(e.target.value)} className="dropdown">
            <option value="">Select Zone</option>
            <option value="North">North</option>
            <option value="South">South</option>
            <option value="East">East</option>
            <option value="West">West</option>
          </select>
        </div>
        <div>
          <label className="label">Filter by Locality:</label>
          <select value={selectedLocality} onChange={(e) => setSelectedLocality(e.target.value)} className="dropdown">
            <option value="">Select Locality</option>
            {Array.from({ length: 16 }, (_, i) => (
              <option key={i + 1} value={`Ward ${i + 1}`}>{`Ward ${i + 1}`}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Mobile Number Filter */}
      <div>
        <label className="label">Filter by Mobile Number:</label>
        <input type="text" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} className="input-box" />
      </div>

      {/* Complaints List */}
      <div>
        {filteredComplaints.length > 0 ? (
          filteredComplaints.map((complaint) => (
            <div key={complaint.id} className="complaint-card">
              <p><strong>Complaint ID:</strong> <a href={`/complainDetailspage`} className="text-blue-500">{complaint.id}</a></p>
              <p><strong>Type:</strong> {complaint.type}</p>
              <p><strong>Status:</strong> {complaint.status}</p>
              <p><strong>Mobile No:</strong> {complaint.mobile}</p>
              <p><strong>Location:</strong> {complaint.location}</p>
              <p><strong>Zone:</strong> {complaint.zone}</p>
              <p><strong>Locality:</strong> {complaint.locality}</p>
            </div>
          ))
        ) : (
          <p>No complaints found for the selected filters.</p>
        )}
      </div>
    </div>
  );
};

export default ComplainDetails;
