import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import '../App.css';

const ComplainSubmit = () => {
  const { authData } = useContext(AuthContext);
  const navigate = useNavigate();

  // Initialize form data with authData values if available
  const [formData, setFormData] = useState({
    complaintType: '',
    description: '',
    userId: authData?.user?.userID || '',
    mobile: authData?.user?.mobileNumber || '',
    email: authData?.user?.emailID || '',
    location: authData?.user?.location || '',
    document: null,
    photo: null,
  });

  // Update form data if authData changes
  useEffect(() => {
    if (authData) {
      setFormData((prevData) => ({
        ...prevData,
        userId: authData.user.userID || '',
        mobile: authData.user.mobileNumber || '',
        email: authData.user.emailID || '',
        location: authData.user.location || '',
      }));
    }
  }, [authData]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle file uploads
  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create FormData object to handle file uploads
    const formDataToSend = new FormData();
    Object.keys(formData).forEach((key) => {
      formDataToSend.append(key, formData[key]);
    });

    try {
      const response = await axios.post(
        'https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/complaints',
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      console.log('API Response:', response.data);

      if (response.data.success) {
        // Show success notification
        setNotification();
        // Navigate to Home page with userId
        navigate('/Home', { state: { userId: formData.userId } });
      } else {
        console.error('Error:', response.data.message);
      }
    } catch (error) {
      console.error('Error during complaint submission:', error);
    }
  };

  // Show notification after successful submission
  const setNotification = () => {
    setTimeout(() => {
      alert('Complaint Submitted Successfully');
    }, 3000);
  };

  return (
    <div className="complain">
      <div className="formContainer">
        <h1>Submit Complaint</h1>
        <form onSubmit={handleSubmit}>
          <div className="formGroup">
            <label>Complaint Type</label>
            <select name="complaintType" value={formData.complaintType} onChange={handleChange} required>
              <option value="">Select Complaint Type</option>
              <option value="water">Water</option>
              <option value="electricity">Electricity</option>
              <option value="road">Road</option>
              <option value="garbage">Garbage</option>
              <option value="other">Other</option>
            </select>
          </div>

          <div className="formGroup">
            <label>Description</label>
            <textarea
              name="description"
              placeholder="Enter Description"
              value={formData.description}
              onChange={handleChange}
              required
            ></textarea>
          </div>

          <div className="formGroup">
            <label>User ID</label>
            <input type="text" name="userId" placeholder="Enter User ID" value={formData.userId} onChange={handleChange} required />
          </div>

          <div className="formGroup">
            <label>Mobile Number</label>
            <input type="text" name="mobile" placeholder="Enter Mobile Number" value={formData.mobile} onChange={handleChange} required />
          </div>

          <div className="formGroup">
            <label>Email</label>
            <input type="email" name="email" placeholder="Enter Email" value={formData.email} onChange={handleChange} required />
          </div>

          <div className="formGroup">
            <label>Location</label>
            <input type="text" name="location" placeholder="Enter Location" value={formData.location} onChange={handleChange} required />
            <button type="button" className="btnSecondary">Refresh Location</button>
          </div>

          <div className="formGroup">
            <label>Upload Document</label>
            <input type="file" name="document" accept=".pdf,.doc,.docx" onChange={handleFileChange} />
          </div>

          <div className="formGroup">
            <label>Upload Photo</label>
            <input type="file" name="photo" accept="image/*" onChange={handleFileChange} />
          </div>

          <button type="submit" className="btnPrimary">Submit Complaint</button>
        </form>
      </div>
    </div>
  );
};

export default ComplainSubmit;
