import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom"; // If using React Router
import { AuthContext } from '../contexts/AuthContext';
import '../App.css';

const ComplainDetailsPage = () => {
  const { id } = useParams(); // Get complaint ID from URL (if using React Router)
  const { authData } = useContext(AuthContext);
  const [complaint, setComplaint] = useState(null);

  useEffect(() => {
    const fetchComplaintDetails = async () => {
      try {
        const response = await fetch(`https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/auth/complain/${id}`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${authData.token}` // Assuming you have a token in authData
          }
        });

        const data = await response.json();
        console.log("API Response:", data);

        if (data) {
          setComplaint(data);
        } else {
          console.error("Unexpected API response format:", data);
          setComplaint(null);
        }
      } catch (error) {
        console.error("Error fetching complaint details:", error);
        setComplaint(null);
      }
    };

    fetchComplaintDetails();
  }, [id, authData]);

  

  return (
    <div className="complaint-details-container">
      <h2>Complaint Details</h2>

      <div className="complaint-info">
        <p><strong>User ID:</strong> {complaint.userId}</p>
        <p><strong>Complaint Type:</strong> {complaint.type}</p>
        <p><strong>Complaint Status:</strong> {complaint.status}</p>
        <p><strong>Mobile No:</strong> {complaint.mobile}</p>
        <p><strong>Description:</strong> {complaint.description}</p>
        <p><strong>Location:</strong> {complaint.location}</p>
        <p><strong>Created By:</strong> {complaint.createdBy}</p>
        <p><strong>Created Date:</strong> {new Date(complaint.createdDate).toLocaleString()}</p>
        
        {/* Document Link */}
        <p><strong>Document:</strong> 
          <a href={complaint.document} target="_blank" rel="noopener noreferrer" className="download-link">
            Download Document
          </a>
        </p>

        {/* Photo */}
        <p><strong>Photo:</strong></p>
        <img src={complaint.photo} alt="Complaint" className="complaint-photo" />
      </div>

      {/* Action Buttons */}
      <div className="button-group">
        <button className="btn reply">Reply</button>
        <button className="btn close">Close Complaint</button>
        <button className="btn back">Back</button>
      </div>
    </div>
  );
};

export default ComplainDetailsPage;
