import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import '../App.css';

const ComplainSubmit = () => {
  const { authData } = useContext(AuthContext);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    complaintType: '',
    description: '',
    userId: authData?.user?.userID || '',
    mobile: authData?.user?.mobileNumber || '',
    email: authData?.user?.emailID || '',
    location: authData?.user?.location || '',
    document: null,
    photo: null,
  });

  useEffect(() => {
    if (authData) {
      setFormData((prevData) => ({
        ...prevData,
        userId: authData.user.userID || '',
        mobile: authData.user.mobileNumber || '',
        email: authData.user.emailID || '',
        location: authData.user.location || '',
      }));
    }
  }, [authData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fileData = new FormData();
      fileData.append('attachmentDoc', formData.document);
      fileData.append('userImage', formData.photo);

      const fileUploadResponse = await axios.post(
        'https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/submitFiles',
        fileData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );

      if (fileUploadResponse.data.success) {
        const complaintData = {
          complaintType: formData.complaintType,
          description: formData.description,
          userId: formData.userId,
          mobile: formData.mobile,
          email: formData.email,
          location: formData.location,
          documentUrl: fileUploadResponse.data.attachmentDocUrl,
          photoUrl: fileUploadResponse.data.userImageUrl,
        };

        const complaintResponse = await axios.post(
          'https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/complaints',
          complaintData,
          { headers: { 'Content-Type': 'application/json' } }
        );

        if (complaintResponse.data.success) {
          alert('Complaint Submitted Successfully');
          navigate('/Home', { state: { userId: formData.userId } });
        } else {
          console.error('Error:', complaintResponse.data.message);
        }
      } else {
        console.error('File Upload Error:', fileUploadResponse.data.message);
      }
    } catch (error) {
      console.error('Error during complaint submission:', error);
    }
  };

  return (
    <div className="complain">
      <div className="formContainer">
        <h1>Submit Complaint</h1>
        <form onSubmit={handleSubmit}>
          <div className="formGroup">
            <label>Complaint Type</label>
            <select name="complaintType" value={formData.complaintType} onChange={handleChange} required>
              <option value="">Select Complaint Type</option>
              <option value="water">Water</option>
              <option value="electricity">Electricity</option>
              <option value="road">Road</option>
              <option value="garbage">Garbage</option>
              <option value="other">Other</option>
            </select>
          </div>
          <div className="formGroup">
            <label>Description</label>
            <textarea name="description" value={formData.description} onChange={handleChange} required></textarea>
          </div>
          <div className="formGroup">
            <label>Upload Document</label>
            <input type="file" name="document" accept=".pdf,.doc,.docx" onChange={handleFileChange} />
          </div>
          <div className="formGroup">
            <label>Upload Photo</label>
            <input type="file" name="photo" accept="image/*" onChange={handleFileChange} />
          </div>
          <button type="submit" className="btnPrimary">Submit Complaint</button>
        </form>
      </div>
    </div>
  );
};

export default ComplainSubmit;
