import React, { useState, useContext } from 'react';
import axios from 'axios';
import '../App.css';

const UserSurveyDetails = () => {
    const [mobileNumber, setMobileNumber] = useState('');
      const [data, setData] = useState(null);
      const [loading, setLoading] = useState(false);
    
      const handleSearch = async () => {
        if (!mobileNumber) {
          alert('Please enter a mobile number.');
          return;
        }
    
        setLoading(true);

        try {
            const response = await axios.post('https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/auth/data', {
              MobileNumber: mobileNumber,
            });
      
            if (response.data) {
              setData(response.data);
              console.log('Data:', response.data);
            } else {
              alert('No data found.');
            }
          } catch (error) {
            if (error.response && error.response.status === 204) {
              alert('No owner found.');
            } else {
              console.error('Error fetching data:', error);
              alert('An error occurred while fetching data.');
            }
          } finally {
            setLoading(false);
          }
        };




  return (
    <div className="container">
      <div className="content">
        <h1>Find Owner Details</h1>
        <input
          className="input"
          placeholder="Enter Mobile Number"
          value={mobileNumber}
          onChange={(e) => setMobileNumber(e.target.value)}
          type="text"
        />
        <button className="button" onClick={handleSearch} disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>


            


        


      </div>
    </div>    
  )
}

export default UserSurveyDetails;
