import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UsersList = () => {
  const [users, setUsers] = useState([]);
  const [mobileNumber, setMobileNumber] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchUsers = async (mobile = null) => {
    setLoading(true);
    setError('');
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json'
        }
      };
      
      // If mobile number is provided, include it in the request body
      const data = mobile ? { mobileNumber: mobile } : {};
      
      const response = await axios.post(
        'https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/auth/getAllUsersWithRoles',
        data,
        config
      );

      if (response.data.success) {
        setUsers(response.data.users);
      } else {
        setError(response.data.message || 'No users found');
        setUsers([]);
      }
    } catch (err) {
      setError('Failed to fetch users. Please try again.');
      setUsers([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch all users on component mount
  useEffect(() => {
    fetchUsers();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    fetchUsers(mobileNumber);
  };

  const handleReset = () => {
    setMobileNumber('');
    fetchUsers();
  };

  return (
    <div className="users-container">
      <div className="users-box">
        <h1>Users List</h1>
        
        {/* Search Form */}
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            className="search-input"
            placeholder="Enter Mobile Number"
            value={mobileNumber}
            onChange={(e) => setMobileNumber(e.target.value)}
          />
          <button type="submit" className="search-btn">Search</button>
          <button 
            type="button" 
            className="reset-btn" 
            onClick={handleReset}
          >
            Reset
          </button>
        </form>

        {/* Loading State */}
        {loading && <p>Loading users...</p>}

        {/* Error Message */}
        {error && <p className="error-message">{error}</p>}

        {/* Users Table */}
        {!loading && users.length > 0 && (
          <table className="users-table">
            <thead>
              <tr>
                <th>Username</th>
                <th>Mobile</th>
                <th>Email</th>
                <th>Roles</th>
                <th>Active</th>
                <th>Admin</th>
                
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.userID}>
                  <td>{user.username}</td>
                  <td>{user.mobileNumber}</td>
                  <td>{user.emailID}</td>
                  <td>{user.roles.join(', ') || 'No roles'}</td>
                  <td>{user.isActive ? 'Yes' : 'No'}</td>
                  <td>{user.isAdmin ? 'Yes' : 'No'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default UsersList;
