import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from '../contexts/AuthContext';
import '../App.css';

const ComplainDetails = () => {
  const { authData } = useContext(AuthContext);
  const [complaints, setComplaints] = useState([]);
  const [startDate, setStartDate] = useState("2024-01-01T06:26:42.714Z");
  const [endDate, setEndDate] = useState("2025-03-18T06:26:42.714Z");
  const [selectedType, setSelectedType] = useState("");
  const [selectedStatus, setSelectedStatus] = useState("Open");
  const [selectedZone, setSelectedZone] = useState("");
  const [selectedLocality, setSelectedLocality] = useState("");
  const [filterByAuthNumber, setFilterByAuthNumber] = useState(true);
  const [manualMobileNumber, setManualMobileNumber] = useState("");

  useEffect(() => {
    if (!authData?.user?.mobileNumber || !authData?.user?.username) {
      console.error("User data missing:", authData);
      return;
    }

    const fetchComplaints = async () => {
      try {
        const requestBody = {
          startDate,
          endDate,
          mobileNumber: filterByAuthNumber ? authData.user.mobileNumber : manualMobileNumber,
          createdBy: authData.user.username,
          isAdmin: authData.user.isAdmin,
          complaintType: selectedType,
          complaintStatus: selectedStatus,
          zone: selectedZone,
          locality: selectedLocality,
        };

        const response = await fetch("https://babralaapi-d3fpaphrckejgdd5.centralindia-01.azurewebsites.net/auth/complain", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${authData.token}`
          },
          body: JSON.stringify(requestBody),
        });

        const data = await response.json();
        setComplaints(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error("Error fetching complaints:", error);
        setComplaints([]);
      }
    };

    fetchComplaints();
  }, [startDate, endDate, selectedType, selectedStatus, selectedZone, selectedLocality, filterByAuthNumber, manualMobileNumber, authData]);

  const filteredComplaints = complaints.filter((complaint) => {
    const complaintDate = new Date(complaint.CreatedDate);
    return (
      complaintDate >= new Date(startDate) &&
      complaintDate <= new Date(endDate) &&
      (selectedType ? complaint.ComplaintsType === selectedType : true) &&
      (selectedStatus ? complaint.ComplaintsStatus === selectedStatus : true) &&
      (selectedZone ? complaint.zone === selectedZone : true) &&
      (selectedLocality ? complaint.locality === selectedLocality : true) &&
      ((filterByAuthNumber ? authData.user.mobileNumber : manualMobileNumber) ? 
        complaint.MobileNo.includes(filterByAuthNumber ? authData.user.mobileNumber : manualMobileNumber) : true)
    );
  });

  return (
    <div className="complaint-details-container">
      <h2>Complaint Details</h2>

      <div className="flex-container">
        <div>
          <label className="label">Start Date:</label>
          <input type="date" value={startDate.split('T')[0]} onChange={(e) => setStartDate(e.target.value + "T06:26:42.714Z")} className="input-box" />
        </div>
        <div>
          <label className="label">End Date:</label>
          <input type="date" value={endDate.split('T')[0]} onChange={(e) => setEndDate(e.target.value + "T06:26:42.714Z")} className="input-box" />
        </div>
      </div>

      <div className="flex-container">
        <div>
          <label className="label">Complaint Type:</label>
          <select value={selectedType} onChange={(e) => setSelectedType(e.target.value)} className="dropdown">
            <option value="">All</option>
            <option value="electricity">Electricity</option>
            <option value="water">Water</option>
            <option value="road">Road</option>
            <option value="waste">Waste</option>
            <option value="other">Other</option>
          </select>
        </div>
        <div>
          <label className="label">Complaint Status:</label>
          <select value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)} className="dropdown">
            <option value="">All</option>
            <option value="Open">Open</option>
            <option value="Closed">Closed</option>
          </select>
        </div>
      </div>

      {authData.user.isAdmin && (
        <div>
          <label className="label">Filter by Mobile Number:</label>
          <div>
            <input type="checkbox" checked={filterByAuthNumber} onChange={() => setFilterByAuthNumber(!filterByAuthNumber)} />
            <span> Use my mobile number</span>
          </div>
          {!filterByAuthNumber && (
            <input type="text" value={manualMobileNumber} onChange={(e) => setManualMobileNumber(e.target.value)} className="input-box" placeholder="Enter mobile number" />
          )}
        </div>
      )}

      <div>
        {filteredComplaints.length > 0 ? (
          filteredComplaints.map((complaint) => (
            <div key={complaint.ComplaintID} className="complaint-card">
              <p><strong>Complaint ID:</strong> <a href={`/complainDetailspage`} className="text-blue-500">{complaint.ComplaintID}</a></p>
              <p><strong>Type:</strong> {complaint.ComplaintsType}</p>
              <p><strong>Status:</strong> {complaint.ComplaintsStatus}</p>
              <p><strong>Mobile No:</strong> {complaint.MobileNo}</p>
              <p><strong>Location:</strong> {complaint.Location}</p>
              <p><strong>Zone:</strong> {complaint.zone}</p>
              <p><strong>Locality:</strong> {complaint.locality}</p>
            </div>
          ))
        ) : (
          <p>No complaints found for the selected filters.</p>
        )}
      </div>
    </div>
  );
};

export default ComplainDetails;
