import React, { useState } from 'react';

const Payment = () => {
  const [showQRCode, setShowQRCode] = useState(false);

  const toggleQR = () => {
    setShowQRCode(!showQRCode);
  };

  return (
    <div>
      <div className="Payment">
        <h1>Payment Options</h1>
        <div className="upi">
          <label>Pay through UPI ID</label>
          <input name="UPI" type="text" id='Upi-box' />
          <button type="button" className="btnPrimary">Pay</button>
        </div>
        <div className="Bank Transfer">
          <label>Pay through Bank Transfer</label>
          <input name="Bank" type="text" id='Bank-box' />
          <button type="button" className="btnPrimary">Pay</button>
        </div>
        <div className="QR Code">
          <label>Pay through QR Code</label>
          <button type="button" id='QR-code' onClick={toggleQR}>
            {showQRCode ? "Hide QR code" : "Show QR code"}
          </button>
          {showQRCode && (
            <div className="qr-code-image">
              {/* Replace with your actual QR code image */}
              <img src="path/to/your/qr-code.png" alt="QR Code" />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Payment;
